import * as THREE from "three";
import React, { useState, useRef, useEffect, Suspense } from "react";
import { OrbitControls } from "drei";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { Canvas, useFrame, useLoader } from "react-three-fiber";
import "./App.css";

let frontRightLatch
let rearRightLatch
let frontLeftLatch
let rearLeftLatch 

function Tesla() {
  const ref = useRef();
  const gltf = useLoader(GLTFLoader, "/tesla-w-animations.gltf");
  gltf.scene.scale.set(0.005, 0.005, 0.005)

  let animations
  let mixer
  // let [part1, part2, part3, part5, part6, part7, part8, part9, part10]
  let part4


  if (gltf.animations.length) {
      mixer = new THREE.AnimationMixer(gltf.scene);
      animations = gltf.animations
      console.log(animations)
      // gltf.animations.forEach(clip => {
      //     const action = mixer.clipAction(clip)
      //     action.play();
          // console.log(action)
      // });

      const clipA = animations [ 4 ]
      part4 = mixer.clipAction(clipA)
      if(frontRightLatch === "LATCH_OPENED"){

        part4.paused = false;
        part4.timeScale = 1;

        part4.setLoop(THREE.LoopOnce );
        part4.clampWhenFinished = true;
        part4.play()
      }


  }

  useFrame((state, delta) => { mixer.update(delta) })

  return <primitive object={gltf.scene} position={[0, 0, 0]} />;
}


function Scene() {
  return (
    <>
      <ambientLight />
      <pointLight intensity={0.6} position={[0, 10, 4]} />
      <Suspense fallback={null}>

        <Tesla />
      </Suspense>
      <OrbitControls />


    </>
  );
}

function App() {

  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
  fetch("http://sla.canbushack.com/v1/signals/")
  .then((response) => {
  if (response.ok) {
  return response.json();
  }
  throw response;

})

  .then (data => {
  setData(data);
  console.log(data)
    frontRightLatch = data[0]['VCRIGHT_frontLatchStatus']['enumerated_value']
    rearRightLatch = data[0]['VCRIGHT_rearLatchStatus']['enumerated_value']
    frontLeftLatch = data[1]['VCLEFT_frontLatchStatus']['enumerated_value']
    rearLeftLatch = data[1]['VCLEFT_rearLatchStatus']['enumerated_value']
  })


  .catch((error) => {
  console.error("Error fetching data: ", error);
  setError(error);
  })
  .finally(() => {
  setLoading(false);
  });
  }, []);
  if (loading) return "Application Starting...";
  if (error) return "Error!";

  return (
    <>
      <Canvas
        camera={{
          position: [-5.10923594025578253, 2.3585518996952866, -5.686206208293889],
          near: 0.5,
          far: 2000,
          fov: 25,


        }}
      >
        <Scene />
      </Canvas>


    </>
  );
}

export default App;
