import * as THREE from "three";
import React, { useState, useRef, useEffect, Suspense } from "react";
import { OrbitControls } from "drei";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { Canvas, useFrame, useLoader } from "react-three-fiber";
import "./App.css";

let RearRightLatch;



function Tesla() {
  const ref = useRef();
  const gltf = useLoader(GLTFLoader, "/tesla-w-animations.gltf");
  gltf.scene.scale.set(0.005, 0.005, 0.005)

  let mixer
  let animations
  let partA
  let partB
  let partC
  let partD 



        animations = gltf.animations
        mixer = new THREE.AnimationMixer(gltf.scene);
        // console.log(animations)



    useFrame((state, delta) => {

      const clipA = animations[ 4 ]
      const clipB = animations[ 5 ]
      const clipC = animations[ 2 ]
      const clipD = animations[ 3 ]

      partB = mixer.clipAction(clipB)
      if(RearRightLatch === "LATCH_OPENED2"){
        partB.paused = false;
        partB.timeScale = 1;
        partB.setLoop(THREE.LoopOnce);
        partB.clampWhenFinished = true;
        partB.play()
      }
      else if(RearRightLatch === "LATCH_CLOSED"){
        partB.paused = false;
        partB.timeScale = 1;
        partB.setLoop(THREE.LoopOnce);
        partB.clampWhenFinished = true;
        partB.play()
      }





        //Update
        mixer.update(delta)
    })

  return <primitive object={gltf.scene} position={[0, 0, 0]} />;
}


function Scene() {
  return (
    <>
      <ambientLight />
      <pointLight intensity={0.6} position={[0, 10, 4]} />
      <Suspense fallback={null}>

        <Tesla />

      </Suspense>
      <OrbitControls />


    </>
  );
}


function App() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);


  useEffect(() => {

    Promise.all([
      fetch('http://sla.canbushack.com/db_get/259/'), // Right Side Doors
      fetch('http://sla.canbushack.com/db_get/258/') // Left Side Doors
  ]).then(function (responses) {
      // Get a JSON object from each of the responses
      return Promise.all(responses.map(function (response) {
          return response.json();

      }));
  })

  .then(function (data) {
    RearRightLatch = data[0]['VCRIGHT_frontLatchStatus']['enumerated_value']
    console.log(data)
  })

    .catch((error) => {
    console.error("Error fetching data: ", error);
    setError(error);
    })
    .finally(() => {
    setLoading(false);
    });
    }, []);
    if (loading) return "Application Loading...";
    if (error) return "Error!";

 return (
    <>
      <Canvas
        camera={{
          position: [-5.10923594025578253, 2.3585518996952866, -5.686206208293889],
          near: 0.5,
          far: 2000,
          fov: 25,


        }}
      >
        <Scene />
      </Canvas>

    </>
  );
}

export default App;
