import * as THREE from "three";
import React, { useState, useRef, useEffect, Suspense } from "react";
import { OrbitControls } from "drei";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { Canvas, useFrame, useLoader } from "react-three-fiber";
import "./App.css";


function Tesla() {
  const ref = useRef();
  const gltf = useLoader(GLTFLoader, "/tesla-w-animations.gltf");

  let mixer
  if (gltf.animations.length) {
      mixer = new THREE.AnimationMixer(gltf.scene);
      gltf.animations.forEach(clip => {
          const action = mixer.clipAction(clip)
          action.play();
          // console.log(action)
      });
  }

  useFrame((state, delta) => { mixer.update(delta) })

  return <primitive object={gltf.scene} position={[0, 0, 0]} />;
}


function Scene() {
  return (
    <>
      <ambientLight />
      <pointLight intensity={0.6} position={[0, 10, 4]} />
      <Suspense fallback={null}>

        <Tesla />
      </Suspense>
      <OrbitControls />


    </>
  );
}

function App() {

  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
  fetch("http://sla.canbushack.com/v1/signals/APB_watchdogState")
  .then((response) => {
  if (response.ok) {
  return response.json();
  }
  throw response;

})

  .then (data => {
  setData(data);
    // console.log(data)
  })


  .catch((error) => {
  console.error("Error fetching data: ", error);
  setError(error);
  })
  .finally(() => {
  setLoading(false);
  });
  }, []);
  if (loading) return "Application Starting...";
  if (error) return "Error!";

  return (
    <>
      <Canvas
        camera={{
          position: [-5.10923594025578253, 2.3585518996952866, -5.686206208293889],
          near: 10,
          far: -30,
          fov: -0.10923594025578253
        }}
      >
        <Scene />
      </Canvas>


    </>
  );
}

export default App;
